import React, { useState } from "react";

const AdmissionForm = () => {
  const [formData, setFormData] = useState({
    studentName: "",
    course: "",
    board: "",
    subjects: "",
    joiningDate: "",
    address: "",
    contactNo: "",
    fees: "",
  });

  const [errors, setErrors] = useState({});
  const [responseMessage, setResponseMessage] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const boards = ["CBSE", "ICSE", "State Board", "IB", "Cambridge"];
  const courses = ["Engineering", "Medical", "Commerce", "Arts", "Science"];

  const validate = (name, value) => {
    switch (name) {
      case "studentName":
        if (!value.trim()) return "Student name is required.";
        if (value.length < 3) return "Name must be at least 3 characters.";
        break;
      case "course":
        if (!value) return "Course is required.";
        break;
      case "board":
        if (!value) return "Board is required.";
        break;
      case "subjects":
        if (!value.trim()) return "Subjects are required.";
        break;
      case "joiningDate":
        if (!value) return "Joining date is required.";
        break;
      case "address":
        if (!value.trim()) return "Address is required.";
        if (value.length < 10) return "Address must be at least 10 characters.";
        break;
      case "contactNo":
        if (!value) return "Contact number is required.";
        const phoneRegex = /^[0-9]{10}$/;
        if (!phoneRegex.test(value)) return "Invalid contact number. Must be 10 digits.";
        break;
      case "fees":
        if (!value) return "Fees are required.";
        if (isNaN(value)) return "Fees must be a valid number.";
        break;
      default:
        return null;
    }
    return null;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: validate(name, value) });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate all fields before submitting
    const validationErrors = Object.keys(formData).reduce((acc, key) => {
      const error = validate(key, formData[key]);
      if (error) acc[key] = error;
      return acc;
    }, {});

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    setIsSubmitting(true);
    try {
      const response = await fetch("http://localhost:8000/admission", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error("Failed to submit admission form");

      // Reset form and show success message
      setResponseMessage("Admission form submitted successfully!");
      setFormData({
        studentName: "",
        course: "",
        board: "",
        subjects: "",
        joiningDate: "",
        address: "",
        contactNo: "",
        fees: "",
      });
      setErrors({});
    } catch (error) {
      setResponseMessage("An error occurred. Please try again later.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="max-w-lg mx-auto bg-white p-8 rounded-lg shadow-lg mt-10">
      <h2 className="text-3xl font-semibold text-center text-blue-700 mb-8">Admission Form</h2>
      {responseMessage && (
        <p className="text-center text-green-500 mb-4">{responseMessage}</p>
      )}
      <form className="space-y-6" onSubmit={handleSubmit}>
        {/* Form fields here as before */}
        <button
          type="submit"
          className={`w-full bg-blue-600 text-white py-3 rounded-lg text-sm ${isSubmitting ? "bg-blue-400 cursor-not-allowed" : "hover:bg-blue-700"}`}
          disabled={isSubmitting}
        >
          {isSubmitting ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
};

export default AdmissionForm;
