import React, { useState } from "react";

const AdmissionForm = () => {
  const [formData, setFormData] = useState({
    student_name: "",
    course: "",
    board: "",
    standard: "",
    joining_date: "",
    address: "",
    contact_no: "",
    admission_type: "Offline Admission",
    fees: "",
    installments: 0,
    installment_amounts: [],
    due_dates: [],
  });

  const [errors, setErrors] = useState({});
  const [responseMessage, setResponseMessage] = useState(null);

  const courses = [
    "Mathematics",
    "Science",
    "English",
    "History",
    "Geography",
    "Physics",
    "Chemistry",
  ];
  const boards = ["CBSE", "ICSE", "State Board", "IB", "Cambridge"];
  const standards = [
    "10th Standard",
    "11th Standard",
    "12th Standard",
    "Graduate",
    "Postgraduate",
  ];

  // Validation for different fields
  const validate = (name, value) => {
    switch (name) {
      case "student_name":
        if (!value.trim()) return "Student name is required.";
        if (value.length < 3) return "Name must be at least 3 characters.";
        break;
      case "course":
        if (!value) return "Course is required.";
        break;
      case "board":
        if (!value) return "Board is required.";
        break;
      case "standard":
        if (!value) return "Standard is required.";
        break;
      case "contact_no":
        if (!value) return "Contact number is required.";
        const phoneRegex = /^[0-9]{10}$/;
        if (!phoneRegex.test(value)) return "Invalid phone number. Must be 10 digits.";
        break;
      case "joining_date":
        if (!value) return "Joining date is required.";
        break;
      case "address":
        if (!value) return "Address is required.";
        break;
      case "fees":
        if (!value) return "Fees is required.";
        if (isNaN(value) || parseFloat(value) <= 0) return "Invalid fee amount.";
        break;
      case "installments":
        if (!value || value <= 0) return "Number of installments must be greater than 0.";
        break;
      default:
        return null;
    }
    return null;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: validate(name, value) });

    if (name === "installments" && value > 0) {
      // Calculate installment amounts based on the total fees
      const installmentAmount = (formData.fees / value).toFixed(2);
      setFormData((prevData) => ({
        ...prevData,
        installments: value,
        installment_amounts: Array(Number(value)).fill(installmentAmount),
        due_dates: Array(Number(value)).fill(""),
      }));
    }
  };

  const handleDateChange = (index, date) => {
    const newDueDates = [...formData.due_dates];
    newDueDates[index] = date;
    setFormData({ ...formData, due_dates: newDueDates });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const validationErrors = Object.keys(formData).reduce((acc, key) => {
      const error = validate(key, formData[key]);
      if (error) acc[key] = error;
      return acc;
    }, {});

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/admissions/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) throw new Error("Failed to submit admission");
      setResponseMessage("Your admission has been submitted successfully!");
      setFormData({
        student_name: "",
        course: "",
        board: "",
        standard: "",
        joining_date: "",
        address: "",
        contact_no: "",
        fees: "",
        installments: 0,
        installment_amounts: [],
        due_dates: [],
      });
      setErrors({});
    } catch (error) {
      setResponseMessage("An error occurred. Please try again later.");
    }
  };

  return (
    <div className="max-w-lg mx-auto bg-white p-6 rounded shadow mt-10 mb-10">
      <h2 className="text-3xl font-bold text-center mb-6 text-blue-600">Admission Form</h2>
      {responseMessage && <p className="text-center text-green-500 mb-4">{responseMessage}</p>}
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Other fields for Student Name, Course, Board, etc. */}

        <div>
          <label className="block text-gray-700 mb-1">Fees</label>
          <input
            type="number"
            name="fees"
            value={formData.fees}
            onChange={handleChange}
            className={`border w-full p-1 pl-3 rounded ${
              errors.fees ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter fee amount"
          />
          {errors.fees && <p className="text-red-500 text-sm mt-1">{errors.fees}</p>}
        </div>

        <div>
          <label className="block text-gray-700 mb-1">Number of Installments</label>
          <input
            type="number"
            name="installments"
            value={formData.installments}
            onChange={handleChange}
            className={`border w-full p-1 pl-3 rounded ${
              errors.installments ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter number of installments"
          />
          {errors.installments && <p className="text-red-500 text-sm mt-1">{errors.installments}</p>}
        </div>

        {formData.installments > 0 && (
          <div>
            <h3 className="text-gray-700 mb-3">Installment Details</h3>
            {formData.installment_amounts.map((amount, index) => (
              <div key={index} className="flex space-x-4 mb-3">
                <div className="flex-1">
                  <label className="block text-gray-700 mb-1">Installment {index + 1} Amount</label>
                  <input
                    type="text"
                    value={`$${amount}`}
                    readOnly
                    className="border w-full p-1 pl-3 rounded border-gray-300"
                  />
                </div>
                <div className="flex-1">
                  <label className="block text-gray-700 mb-1">Due Date</label>
                  <input
                    type="date"
                    value={formData.due_dates[index]}
                    onChange={(e) => handleDateChange(index, e.target.value)}
                    className={`border w-full p-1 pl-3 rounded ${
                      errors.due_dates ? "border-red-500" : "border-gray-300"
                    }`}
                  />
                </div>
              </div>
            ))}
          </div>
        )}

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-3 rounded hover:bg-blue-700"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default AdmissionForm;
