"use client";
import React, { useState } from "react";
import logo from "../assets/logo.png";
import AdminCourses from "./AdminCourses"; // Import AdminCourses
import AdminEnquiry from "./AdminEnquiry"; // Import AdminEnquiry

export function AdminPanel() {
  const [currentView, setCurrentView] = useState("home"); // State to track the current view

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar Section */}
      <div className="flex flex-col md:flex-row justify-between items-center h-auto px-6 py-4 bg-blue-500 text-white shadow-lg animate__animated animate__fadeInDown">
        {/* Logo Section */}
        <div className="flex items-center space-x-4">
          <img
            src={logo}
            alt="admin-logo"
            className="h-14 w-14 cursor-pointer transform transition duration-300 hover:scale-110"
          />
          <h1 className="text-2xl text-black font-semibold">Tesla Academy Admin</h1>
        </div>

        {/* Buttons Section */}
        <div className="flex justify-center items-center space-x-4">
          <button
            onClick={() => setCurrentView("courses")}
            className={`${
              currentView === "courses" ? "shadow-lg scale-105" : ""
            } bg-white text-black rounded-full px-6 py-3 font-medium transform transition-all duration-300 hover:scale-110 hover:shadow-red-400 hover:border hover:border-yellow-400 hover:outline-golden`}
          >
            Courses Data
          </button>
          <button
            onClick={() => alert("Button 2 Functionality - Coming Soon!")}
            className="bg-white text-black rounded-full px-6 py-3 font-medium transform transition-all duration-300 hover:scale-110 hover:shadow-red-400 hover:border hover:border-yellow-400 hover:outline-golden"
          >
            Students Data
          </button>
          <button
            onClick={() => setCurrentView("enquiries")}
            className={`${
              currentView === "enquiries" ? "shadow-lg scale-105" : ""
            } bg-white text-black rounded-full px-6 py-3 font-medium transform transition-all duration-300 hover:scale-110 hover:shadow-red-400 hover:border hover:border-yellow-400 hover:outline-golden`}
          >
            Enquiry Data
          </button>
          <button
            onClick={() => alert("Button 4 Functionality - Coming Soon!")}
            className="bg-white text-black rounded-full px-6 py-3 font-medium transform transition-all duration-300 hover:scale-110 hover:shadow-blue-400 hover:border hover:border-yellow-400 hover:outline-golden"
          >
            User Interface
          </button>
        </div>
      </div>

      {/* Main Content Section */}
      <div className="flex flex-col items-center justify-center p-6">
        {currentView === "home" && (
          <div className="text-center mt-10">
            <h1 className="text-4xl font-bold text-gray-700 mb-4">Welcome Admin!</h1>
            <p className="text-gray-500">Use the navigation buttons above to manage data.</p>
          </div>
        )}
        {currentView === "courses" && (
          <div className="w-full">
            <button
              onClick={() => setCurrentView("home")}
              className="bg-gray-500 text-white px-4 py-2 rounded-full m-4 hover:bg-gray-600 transform transition-all duration-300"
            >
              Back to Admin Panel
            </button>
            <AdminCourses />
          </div>
        )}
        {currentView === "enquiries" && (
          <div className="w-full">
            <button
              onClick={() => setCurrentView("home")}
              className="bg-gray-500 text-white px-4 py-2 rounded-full m-4 hover:bg-gray-600 transform transition-all duration-300"
            >
              Back to Admin Panel
            </button>
            <AdminEnquiry />
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminPanel;
