import React, { useState } from "react";

const UserAdmissionForm = () => {
  const [formData, setFormData] = useState({
    student_name: "",
    course: "",
    board: "",
    standard: "",
    joining_date: "",
    address: "",
    contact_no: "",
  });

  const [errors, setErrors] = useState({});
  const [responseMessage, setResponseMessage] = useState(null);
  const [paymentCompleted, setPaymentCompleted] = useState(false);

  const courses = [
    "Mathematics",
    "Science",
    "English",
    "History",
    "Geography",
    "Physics",
    "Chemistry",
  ];
  const boards = ["CBSE", "ICSE", "State Board", "IB", "Cambridge"];
  const standards = [
    "10th Standard",
    "11th Standard",
    "12th Standard",
    "Graduate",
    "Postgraduate",
  ];

  const validate = (name, value) => {
    switch (name) {
      case "student_name":
        if (!value.trim()) return "Student name is required.";
        if (value.length < 3) return "Name must be at least 3 characters.";
        break;
      case "course":
        if (!value) return "Course is required.";
        break;
      case "board":
        if (!value) return "Board is required.";
        break;
      case "standard":
        if (!value) return "Standard is required.";
        break;
      case "contact_no":
        if (!value) return "Contact number is required.";
        const phoneRegex = /^[0-9]{10}$/;
        if (!phoneRegex.test(value)) return "Invalid phone number. Must be 10 digits.";
        break;
      case "joining_date":
        if (!value) return "Joining date is required.";
        break;
      case "address":
        if (!value) return "Address is required.";
        break;
      default:
        return null;
    }
    return null;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: validate(name, value) });
  };

  const handlePayment = async () => {
    try {
      // Simulate payment processing API call
      const paymentResponse = await fetch("http://localhost:8000/process-payment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ course: formData.course, standard: formData.standard }),
      });

      if (!paymentResponse.ok) throw new Error("Payment failed.");
      const paymentData = await paymentResponse.json();

      setFormData({ ...formData, fees: paymentData.fees });
      setPaymentCompleted(true);
    } catch (error) {
      setResponseMessage("Payment failed. Please try again.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!paymentCompleted) {
      setResponseMessage("Please complete the payment first.");
      return;
    }

    const validationErrors = Object.keys(formData).reduce((acc, key) => {
      const error = validate(key, formData[key]);
      if (error) acc[key] = error;
      return acc;
    }, {});

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/admissions/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) throw new Error("Failed to submit admission");
      setResponseMessage("Your admission has been submitted successfully!");
      setFormData({
        student_name: "",
        course: "",
        board: "",
        standard: "",
        joining_date: "",
        address: "",
        contact_no: "",
      });
      setErrors({});
      setPaymentCompleted(false);
    } catch (error) {
      setResponseMessage("An error occurred. Please try again later.");
    }
  };

  return (
    <div className="max-w-lg mx-auto bg-white p-6 rounded shadow mt-10">
      <h2 className="text-3xl font-bold text-center mb-6 text-blue-600">Admission Form</h2>
      {responseMessage && <p className="text-center text-green-500 mb-4">{responseMessage}</p>}
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-gray-700 mb-1">Student Name</label>
          <input
            type="text"
            name="student_name"
            value={formData.student_name}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.student_name ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter student name"
          />
          {errors.student_name && (
            <p className="text-red-500 text-sm mt-1">{errors.student_name}</p>
          )}
        </div>
        {/* Repeat similar blocks for other inputs */}

        <button
          type="button"
          className="w-full bg-yellow-600 text-white py-3 rounded hover:bg-yellow-700"
          onClick={handlePayment}
        >
          Proceed to Payment
        </button>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-3 rounded hover:bg-blue-700"
          disabled={!paymentCompleted}
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default UserAdmissionForm;
