"use client";

import React, { useState } from "react";

const AdmissionForm = () => {
  const [formData, setFormData] = useState({
    studentName: "",
    course: "",
    board: "",
    subjects: "",
    joiningDate: "",
    address: "",
    contactNo: "",
    fees: "",
  });

  const [errors, setErrors] = useState({});
  const [responseMessage, setResponseMessage] = useState(null);

  const boards = ["CBSE", "ICSE", "State Board", "IB", "Cambridge"];
  const courses = ["Engineering", "Medical", "Commerce", "Arts", "Science"];

  const validate = (name, value) => {
    switch (name) {
      case "studentName":
        if (!value.trim()) return "Student name is required.";
        if (value.length < 3) return "Name must be at least 3 characters.";
        break;
      case "course":
        if (!value) return "Course is required.";
        break;
      case "board":
        if (!value) return "Board is required.";
        break;
      case "subjects":
        if (!value.trim()) return "Subjects are required.";
        break;
      case "joiningDate":
        if (!value) return "Joining date is required.";
        break;
      case "address":
        if (!value.trim()) return "Address is required.";
        if (value.length < 10) return "Address must be at least 10 characters.";
        break;
      case "contactNo":
        if (!value) return "Contact number is required.";
        const phoneRegex = /^[0-9]{10}$/;
        if (!phoneRegex.test(value)) return "Invalid contact number. Must be 10 digits.";
        break;
      case "fees":
        if (!value) return "Fees are required.";
        if (isNaN(value)) return "Fees must be a valid number.";
        break;
      default:
        return null;
    }
    return null;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: validate(name, value) });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate all fields before submitting
    const validationErrors = Object.keys(formData).reduce((acc, key) => {
      const error = validate(key, formData[key]);
      if (error) acc[key] = error;
      return acc;
    }, {});

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/admission/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error("Failed to submit admission form");

      // Reset form and show success message
      setResponseMessage("Admission form submitted successfully!");
      setFormData({
        studentName: "",
        course: "",
        board: "",
        subjects: "",
        joiningDate: "",
        address: "",
        contactNo: "",
        fees: "",
      });
      setErrors({});
    } catch (error) {
      setResponseMessage("An error occurred. Please try again later.");
    }
  };

  return (
    <div className="max-w-lg mx-auto bg-white p-6 rounded shadow mt-10">
      <h2 className="text-3xl font-bold text-center mb-6 text-blue-600">Admission Form</h2>
      {responseMessage && <p className="text-center text-green-500 mb-4">{responseMessage}</p>}
      <form className="space-y-6" onSubmit={handleSubmit}>
        <div>
          <label className="block text-gray-700 mb-1">Student Name</label>
          <input
            type="text"
            name="studentName"
            value={formData.studentName}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.studentName ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter student name"
          />
          {errors.studentName && <p className="text-red-500 text-sm mt-1">{errors.studentName}</p>}
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Course</label>
          <select
            name="course"
            value={formData.course}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.course ? "border-red-500" : "border-gray-300"
            }`}
          >
            <option value="">Select Course</option>
            {courses.map((course) => (
              <option key={course} value={course}>
                {course}
              </option>
            ))}
          </select>
          {errors.course && <p className="text-red-500 text-sm mt-1">{errors.course}</p>}
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Board</label>
          <select
            name="board"
            value={formData.board}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.board ? "border-red-500" : "border-gray-300"
            }`}
          >
            <option value="">Select Board</option>
            {boards.map((board) => (
              <option key={board} value={board}>
                {board}
              </option>
            ))}
          </select>
          {errors.board && <p className="text-red-500 text-sm mt-1">{errors.board}</p>}
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Subjects</label>
          <input
            type="text"
            name="subjects"
            value={formData.subjects}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.subjects ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter subjects"
          />
          {errors.subjects && <p className="text-red-500 text-sm mt-1">{errors.subjects}</p>}
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Joining Date</label>
          <input
            type="date"
            name="joiningDate"
            value={formData.joiningDate}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.joiningDate ? "border-red-500" : "border-gray-300"
            }`}
          />
          {errors.joiningDate && <p className="text-red-500 text-sm mt-1">{errors.joiningDate}</p>}
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Address</label>
          <textarea
            name="address"
            value={formData.address}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.address ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter address"
            rows="4"
          />
          {errors.address && <p className="text-red-500 text-sm mt-1">{errors.address}</p>}
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Contact No.</label>
          <input
            type="tel"
            name="contactNo"
            value={formData.contactNo}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.contactNo ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter contact number"
          />
          {errors.contactNo && <p className="text-red-500 text-sm mt-1">{errors.contactNo}</p>}
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Fees</label>
          <input
            type="text"
            name="fees"
            value={formData.fees}
            onChange={handleChange}
            className={`border w-full p-3 rounded ${
              errors.fees ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter fees"
          />
          {errors.fees && <p className="text-red-500 text-sm mt-1">{errors.fees}</p>}
        </div>
        <button
          type="submit"
          onClick={handleSubmit}
          className="w-full bg-blue-600 text-white py-3 rounded hover:bg-blue-700"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default AdmissionForm;
