import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios'; // for data fetching

const Forum = () => {
  const [categories, setCategories] = useState([]);
  const [topicsData, setTopicsData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loading, setLoading] = useState(true);

  // Fetch categories and topics data from API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const categoriesResponse = await axios.get('/api/categories');
        const topicsResponse = await axios.get('/api/topics');
        setCategories(categoriesResponse.data);
        setTopicsData(topicsResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setSelectedTopic(null); // Reset topic when selecting a new category
  };

  const handleTopicClick = (topic) => {
    setSelectedTopic(topic);
    // Fetch comments dynamically from API (if needed)
    setComments([
      { id: 1, text: 'This is a great topic!', user: 'John Doe' },
      { id: 2, text: 'I agree with this!', user: 'Jane Smith' }
    ]); // Example of default comments
  };

  const handleAddComment = async () => {
    if (!newComment.trim()) return; // Don't add empty comments

    // Send new comment to the server (for dynamic backend interaction)
    try {
      const commentData = { text: newComment, user: 'Anonymous' };
      // POST request to API to add comment
      await axios.post(`/api/topics/${selectedTopic.id}/comments`, commentData);
      setComments([...comments, { ...commentData, id: comments.length + 1 }]);
      setNewComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const filteredTopics = selectedCategory
    ? topicsData.filter((topic) => topic.categoryId === selectedCategory.id)
    : [];

  return (
    <div className="bg-gray-100 min-h-screen p-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold mb-6 text-center text-gray-800">Tesla Academy Forum</h1>

        {/* Loading State */}
        {loading && (
          <div className="text-center">
            <p className="text-xl text-gray-600">Loading forum data...</p>
          </div>
        )}

        {/* Categories Section */}
        {!loading && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-12">
            {categories.map((category) => (
              <button
                key={category.id}
                onClick={() => handleCategoryClick(category)}
                className="bg-indigo-600 text-white p-6 rounded-lg shadow-lg hover:bg-indigo-700 transition duration-300"
              >
                <h2 className="text-2xl font-semibold">{category.name}</h2>
              </button>
            ))}
          </div>
        )}

        {/* Topics Section */}
        {selectedCategory && !loading && (
          <div className="mt-8">
            <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">{selectedCategory.name}</h2>
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <ul>
                {filteredTopics.map((topic) => (
                  <li
                    key={topic.id}
                    className="border-b py-4 cursor-pointer hover:bg-gray-100"
                    onClick={() => handleTopicClick(topic)}
                  >
                    <div className="flex justify-between">
                      <h3 className="text-xl font-semibold text-gray-700">{topic.title}</h3>
                      <span className="text-sm text-gray-500">{topic.replies} replies</span>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        )}

        {/* Discussion Section */}
        {selectedTopic && (
          <div className="mt-12 bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold mb-4 text-gray-800">{selectedTopic.title}</h2>

            {/* Comments */}
            <ul className="mb-6">
              {comments.map((comment) => (
                <li key={comment.id} className="border-b py-4">
                  <p className="font-semibold text-gray-700">{comment.user}</p>
                  <p className="text-gray-600">{comment.text}</p>
                </li>
              ))}
            </ul>

            {/* Add Comment */}
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add your comment"
              className="w-full p-4 border border-gray-300 rounded-lg mb-4 focus:ring-2 focus:ring-indigo-500"
            />
            <button
              onClick={handleAddComment}
              className="bg-teal-600 text-white p-3 rounded-lg hover:bg-teal-700 transition duration-300"
            >
              Post Comment
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Forum;
