import { useState } from "react";

const AddCourse = (props) => {
  const [course, setCourse] = useState({ title: "", description: "", courselink: "", standard: "" });
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:8000/course/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(course),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      props.setModalOpen(false);
      props.fetchCourses();
    } catch (err) {
      console.error("Error:", err);
      setError(err.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCourse((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div>
      <h2>Add Course</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={course.title} onChange={handleChange} required />
        </label>
        <br />
        <label>
          Description:
          <textarea name="description" value={course.description} onChange={handleChange} required />
        </label>
        <br />
        <label>
          Course Link:
          <input type="url" name="courselink" value={course.courselink} onChange={handleChange} required />
        </label>
        <br />
        <label>
          Standard:
          <input type="number" name="standard" value={course.standard} onChange={handleChange} required />
        </label>
        <br />
        <button type="submit">Add Course</button>
      </form>
    </div>
  );
};

export default AddCourse;
