"use client";
import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";

// HoverEffect Component for scaling and shadow effects on hover
const HoverEffect = ({ children }) => (
  <div className="relative overflow-hidden transition-transform transform hover:scale-105 hover:shadow-xl">
    {children}
  </div>
);

export function Courses() {
  const [courses, setCourses] = useState({}); // State to hold grouped courses
  const [error, setError] = useState(null); // State to handle errors
  const [loading, setLoading] = useState(true); // State to handle loading

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true); // Start loading
        const response = await fetch("http://127.0.0.1:8000/courses/");
        if (!response.ok) throw new Error("Failed to fetch courses.");
        const data = await response.json();

        console.log("Fetched courses:", data); // Debug: Log fetched data

        // Ensure the response is in the correct format
        if (data && typeof data === "object") {
          setCourses(data); // Store the grouped courses
        } else {
          throw new Error("Invalid data format received.");
        }
      } catch (error) {
        console.error("Error fetching courses:", error);
        setError(error.message);
      } finally {
        setLoading(false); // Stop loading
      }
    };

    fetchCourses();
  }, []);

  // Render loading state
  if (loading) return <div className="text-center text-blue-500">Loading courses...</div>;

  // Render error state
  if (error) return <div className="text-center text-red-500">{error}</div>;

  // Check if courses is an object and has keys
  if (!Object.keys(courses).length) {
    return <div className="text-center text-gray-500">No courses available.</div>;
  }

  return (
    <section id="courses" className="py-12 bg-gray-100">
      <h2 className="text-4xl font-bold text-center mb-10">COURSES</h2>
      <div className="container mx-auto px-4">
        {Object.entries(courses).map(([standard, courseList]) => (
          <div key={standard} className="mb-12">
            <h3 className="text-3xl font-semibold mb-6">{standard}</h3>
            <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 justify-center">
              {courseList.map((course, index) => (
                <HoverEffect key={index}>
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: 0.2 * index }}
                    className="relative h-60 overflow-hidden rounded-lg shadow-lg"
                  >
                    <img
                      src={course.img}
                      alt={course.title}
                      className="absolute inset-0 w-full h-full object-cover"
                    />
                    <div className="relative z-10 p-4 bg-black bg-opacity-50 h-full flex flex-col justify-center">
                      <h4 className="text-xl font-semibold text-white mb-2">{course.title}</h4>
                      <p className="text-gray-200">{course.description}</p>
                    </div>
                  </motion.div>
                </HoverEffect>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Courses;
