import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { ReactTyped } from "react-typed";
import {
  FaCertificate,
  FaUserGraduate,
  FaClock,
  FaGraduationCap,
  FaBook,
  FaLaptopCode,
  FaPlayCircle,
  FaUser,
  FaPeopleArrows,
  FaCogs,
} from "react-icons/fa";
import { Link } from "react-router-dom";

const Hero = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 500); // Delay before text appears
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="relative flex flex-col items-center justify-center bg-gradient-to-br from-teal-500 to-blue-600 py-12 lg:py-24 px-6">
      {/* Hero Section */}
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        className="text-center w-full max-w-7xl relative z-10"
      >
        <h1 className="text-4xl md:text-5xl lg:text-7xl font-extrabold text-white leading-tight">
          Empowering Minds,
        </h1>
        <h1 className="text-4xl md:text-5xl lg:text-7xl font-extrabold text-yellow-300 mt-4 leading-tight">
          Shaping Futures at
        </h1>
        <h1 className="text-4xl md:text-5xl lg:text-7xl font-extrabold text-white mt-4 leading-tight">
          Tesla Academy.
        </h1>
        <div className="mt-6 text-xl md:text-2xl lg:text-3xl font-semibold text-yellow-200">
          <ReactTyped
            strings={["SCIENCE...", "MATHEMATICS...", "SOCIAL STUDIES...", "COMPUTER SCIENCE...", "ENGLISH..."]}
            typeSpeed={50}
            backSpeed={80}
            loop
          />
        </div>
        <p className="mt-6 text-lg md:text-xl text-gray-200 font-medium">
          Unlock your full potential with our comprehensive learning programs and industry-certified courses.
        </p>
        <div className="mt-8">
          <Link to="/login">
            <motion.button
              whileHover={{ scale: 1.1 }}
              className="bg-yellow-400 text-black px-6 py-3 rounded-full text-lg font-bold shadow-lg hover:shadow-xl transition-all"
            >
              Get Started
            </motion.button>
          </Link>
        </div>
      </motion.div>

      {/* Featured Courses Section */}
      <div className="py-16 bg-gray-50 w-full">
        <div className="text-center mb-10">
          <h2 className="text-4xl lg:text-5xl font-bold text-gray-800">Explore Our Popular Courses</h2>
          <p className="text-gray-600 mt-2 text-sm md:text-base">
            Learn from a wide range of expertly curated courses designed to enhance your knowledge.
          </p>
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 px-6 max-w-7xl mx-auto">
          {["Mathematics", "Science", "Physics", "Social Studies", "Computer Science", "English"].map((course, index) => (
            <motion.div
              whileHover={{ scale: 1.05 }}
              key={index}
              className="border rounded-lg shadow-lg p-4 bg-white hover:shadow-xl transition-all duration-300"
            >
              <img
                src={`https://via.placeholder.com/400x200?text=${course}`}
                alt={course}
                className="rounded-md w-full object-cover h-48 mb-4"
              />
              <h3 className="text-xl lg:text-2xl font-bold text-gray-800">{course}</h3>
              <p className="text-gray-600 mt-2 text-sm md:text-base">
                Master {course} with our expertly curated content and resources.
              </p>
              <button className="mt-4 bg-teal-500 text-white px-4 py-2 rounded-lg font-medium hover:bg-teal-700 transition">
                <Link to="/login">Learn More</Link>
              </button>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Video Preview Section */}
      <div className="py-16 bg-gradient-to-l from-blue-100 to-gray-50 w-full">
        <div className="text-center mb-10">
          <h2 className="text-4xl lg:text-5xl font-bold text-gray-800">Interactive Video Lessons</h2>
          <p className="text-gray-600 mt-2 text-sm md:text-base">
            Watch our engaging lessons designed to simplify complex concepts.
          </p>
        </div>
        <div className="flex justify-center">
          <motion.div whileHover={{ scale: 1.05 }} className="relative group">
            <img
              src="https://via.placeholder.com/800x450"
              alt="Video Preview"
              className="rounded-lg shadow-lg"
            />
            <FaPlayCircle
              size={60}
              className="absolute inset-0 m-auto text-teal-600 group-hover:text-teal-700 transition-all cursor-pointer"
            />
          </motion.div>
        </div>
      </div>

      {/* Why Choose Us Section */}
      <div className="py-12 bg-gradient-to-r from-teal-100 to-blue-100 w-full">
        <div className="text-center mb-10">
          <h2 className="text-3xl lg:text-5xl font-bold text-gray-800">Why Choose Tesla Academy?</h2>
          <p className="text-gray-600 mt-2 text-sm md:text-base">
            Discover the unique features that make us the best choice for your learning journey.
          </p>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 px-6 max-w-7xl mx-auto">
          {[{
            title: 'Certified Courses',
            description: 'Get certified and boost your career with industry-recognized courses.',
            icon: <FaCertificate size={40} className="text-teal-600 mb-4" />,
          },
          {
            title: 'Self-Paced Learning',
            description: 'Learn at your own pace with flexible schedules and access anytime.',
            icon: <FaClock size={40} className="text-teal-600 mb-4" />,
          }].map((feature, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.05 }}
              className="p-6 border rounded-lg shadow-lg bg-white hover:shadow-xl transform transition-all duration-300"
            >
              <div className="flex justify-center">{feature.icon}</div>
              <h3 className="text-lg lg:text-xl font-bold text-teal-600 mb-2 text-center">
                {feature.title}
              </h3>
              <p className="text-gray-600 text-sm md:text-base text-center">{feature.description}</p>
            </motion.div>
          ))}
        </div>
      </div>

      
    </div>
  );
};

export default Hero;
