import React from "react";
import { ReactTyped } from "react-typed";
import hero from "../assets/hero.png";

const Hero = () => {
  return (
    <div className="h-screen relative bg-white flex flex-col lg:flex-row items-center justify-between overflow-hidden">
      {/* Text Content */}
      <div className="text-2xl lg:text-5xl font-bold text-gray-800 z-10 p-6 w-full lg:w-1/2">
        <h1 className="md:text-7xl sm:text-6xl text-3xl p-2 font-bold animate-[fade-in_1s_ease-in-out_forwards]">
          Empowering Minds,
        </h1>
        <h1 className="text-teal-500 md:text-7xl sm:text-6xl text-3xl p-4 font-bold animate-[fade-in_1s_ease-in-out_forwards_0.2s]">
          Shaping Futures at
        </h1>
        <h1 className="md:text-7xl sm:text-6xl text-4xl p-2 font-bold animate-[fade-in_1s_ease-in-out_forwards_0.4s]">
          Tesla Academy.
        </h1>

        <div className="mt-4">
          <ReactTyped
            strings={['SCIENCE...', 'MATHEMATICS...', 'SOCIAL STUDIES...']}
            className="md:text-3xl sm:text-2xl text-2xl p-2 font-bold text-teal-500"
            typeSpeed={50}
            backSpeed={80}
            loop
          />
        </div>
      </div>

      {/* Image Content - Positioned just above the footer */}
      <div className="w-full lg:w-1/2 flex justify-center items-center p-4 mt-auto">
        <img
          src={hero}
          alt="Description of the image"
          className="w-full sm:w-3/4 lg:w-1/2 h-auto object-contain rounded-lg transform hover:scale-105 transition-all duration-300"
        />
      </div>
    </div>
  );
};

export default Hero;
