import React from "react";
import { ReactTyped } from "react-typed";
import hero from "../assets/hero.png";
import blob from '../assets/blob.svg';
import { motion } from "framer-motion";

const Hero = () => {
  return (
    <div 
      className='bg-cover bg-center h-screen relative bg-gray-200' // Grey background
      style={{ backgroundImage: `url(${blob})` }}
    >
      <div className="absolute inset-0 bg-black opacity-50"></div> {/* Overlay to improve text visibility */}
      
      <div className="text-2xl lg:text-5xl font-bold text-white relative z-10">
        <div className="mt-4 text-left p-6">
          <motion.h1 
            className='md:text-7xl sm:text-6xl text-3xl p-2 font-bold text-left'
            initial={{ opacity: 0, y: -100 }} 
            animate={{ opacity: 1, y: 0 }} 
            transition={{ duration: 1 }}
          >
            Empowering Minds,
          </motion.h1>
          <motion.h1 
            className='text-teal-500 md:text-7xl sm:text-6xl text-3xl p-4 font-bold text-left'
            initial={{ opacity: 0, y: -100 }} 
            animate={{ opacity: 1, y: 0 }} 
            transition={{ duration: 1, delay: 0.2 }}
          >
            Shaping Futures at
          </motion.h1>
          <motion.h1 
            className='md:text-7xl sm:text-6xl text-4xl p-2 font-bold text-left'
            initial={{ opacity: 0, y: -100 }} 
            animate={{ opacity: 1, y: 0 }} 
            transition={{ duration: 1, delay: 0.4 }}
          >
            Tesla Academy.
          </motion.h1>

          <div className="mt-4">
            <ReactTyped 
              strings={['SCIENCE...', 'MATHEMATICS...', 'SOCIAL STUDIES...']} 
              className="md:text-3xl sm:text-2xl text-2xl p-2 font-bold text-teal-500 text-left"
              typeSpeed={50} 
              backSpeed={80} 
              loop
            />
          </div>
        </div>
        
        
      </div>

      <motion.div 
          className="absolute bottom-0 right-0 p-6" 
          initial={{ opacity: 0, x: 100 }} 
          animate={{ opacity: 1, x: 0 }} 
          transition={{ duration: 1, delay: 0.8 }}
        >
          <img 
            src={hero} 
            alt="Description of the image" 
            className="w-3/4 md:w-1/2 shadow-lg rounded-lg transform hover:scale-105 transition-all"
          />
        </motion.div>
    </div> 
  );
};

export default Hero;
