"use client";
import React, { useState } from "react";
import logo from "../assets/logo.png";
import AdminCourses from "./AdminCourses"; // Import AdminCourses
import AdminEnquiry from "./AdminEnquiry"; // Import AdminEnquiry

export function AdminPanel() {
  const [currentView, setCurrentView] = useState("home"); // State to track the current view
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // State to handle sidebar toggle

  return (
    <div className="min-h-screen flex bg-gray-100">
      {/* Sidebar Section */}
      <div
        className={`flex flex-col items-center w-64 bg-gradient-to-br from-blue-600 via-teal-500 to-blue-400 text-white shadow-lg p-6 animate__animated animate__fadeInLeft 
        ${isSidebarOpen ? "block" : "hidden"} sm:block`} // Show on mobile with a toggle
      >
        {/* Logo Section */}
        <div className="mb-8">
          <img
            src={logo}
            alt="admin-logo"
            className="h-16 w-16 cursor-pointer transform transition duration-300 hover:scale-110"
          />
          <h1 className="text-2xl font-semibold mt-2 text-center">Tesla Academy</h1>
        </div>

        {/* Navigation Buttons */}
        <nav className="space-y-6 w-full">
          <button
            onClick={() => setCurrentView("home")}
            className={`w-full py-3 rounded-full text-lg font-medium ${
              currentView === "home"
                ? "bg-yellow-400 text-black shadow-lg scale-105"
                : "bg-white text-black"
            } transform transition-all duration-300 hover:scale-105 hover:shadow-yellow-300`}
          >
            Home
          </button>
          <button
            onClick={() => setCurrentView("courses")}
            className={`w-full py-3 rounded-full text-lg font-medium ${
              currentView === "courses"
                ? "bg-yellow-400 text-black shadow-lg scale-105"
                : "bg-white text-black"
            } transform transition-all duration-300 hover:scale-105 hover:shadow-yellow-300`}
          >
            Courses Data
          </button>
          <button
            onClick={() => alert("Students Data - Coming Soon!")}
            className="w-full py-3 rounded-full text-lg font-medium bg-white text-black transform transition-all duration-300 hover:scale-105 hover:shadow-yellow-300"
          >
            Students Data
          </button>
          <button
            onClick={() => setCurrentView("enquiries")}
            className={`w-full py-3 rounded-full text-lg font-medium ${
              currentView === "enquiries"
                ? "bg-yellow-400 text-black shadow-lg scale-105"
                : "bg-white text-black"
            } transform transition-all duration-300 hover:scale-105 hover:shadow-yellow-300`}
          >
            Enquiry Data
          </button>
          <button
            onClick={() => alert("User Interface - Coming Soon!")}
            className="w-full py-3 rounded-full text-lg font-medium bg-white text-black transform transition-all duration-300 hover:scale-105 hover:shadow-yellow-300"
          >
            User Interface
          </button>
        </nav>
      </div>

      {/* Mobile Menu Toggle */}
      <div
        className="sm:hidden absolute top-4 left-4 z-50"
        onClick={() => setIsSidebarOpen(!isSidebarOpen)}
      >
        <button className="text-white text-3xl">
          {isSidebarOpen ? "✖" : "☰"} {/* Hamburger menu */}
        </button>
      </div>

      {/* Main Content Section */}
      <div className="flex-1 p-8 sm:p-6">
        {currentView === "home" && (
          <div className="text-center mt-10">
            <h1 className="text-4xl font-bold text-gray-700 mb-4 animate__animated animate__fadeInUp">
              Welcome to Admin Panel
            </h1>
            <p className="text-gray-500">Use the navigation buttons on the left to manage data.</p>
          </div>
        )}
        {currentView === "courses" && (
          <div className="animate__animated animate__fadeInRight">
            <button
              onClick={() => setCurrentView("home")}
              className="bg-gray-500 text-white px-4 py-2 rounded-full m-4 hover:bg-gray-600 transform transition-all duration-300"
            >
              Back to Admin Panel
            </button>
            <AdminCourses />
          </div>
        )}
        {currentView === "enquiries" && (
          <div className="animate__animated animate__fadeInRight">
            <button
              onClick={() => setCurrentView("home")}
              className="bg-gray-500 text-white px-4 py-2 rounded-full m-4 hover:bg-gray-600 transform transition-all duration-300"
            >
              Back to Admin Panel
            </button>
            <AdminEnquiry />
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminPanel;
