import React from "react";
import { ReactTyped } from "react-typed";
import hero from "../assets/hero.png";
import blob from '../assets/blob.svg';
import { motion } from "framer-motion";

const Hero = () => {
  return (
    <div 
      className="h-screen relative bg-white flex flex-col lg:flex-row items-center justify-between overflow-hidden" // White background
    >
      {/* Text Content */}
      <div className="text-2xl lg:text-5xl font-bold text-gray-800 z-10 p-6 w-full lg:w-1/2">
        <motion.h1 
          className="md:text-7xl sm:text-6xl text-3xl p-2 font-bold"
          initial={{ opacity: 0, y: -50 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 1 }}
        >
          Empowering Minds,
        </motion.h1>
        <motion.h1 
          className="text-teal-500 md:text-7xl sm:text-6xl text-3xl p-4 font-bold"
          initial={{ opacity: 0, y: -50 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 1, delay: 0.2 }}
        >
          Shaping Futures at
        </motion.h1>
        <motion.h1 
          className="md:text-7xl sm:text-6xl text-4xl p-2 font-bold"
          initial={{ opacity: 0, y: -50 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 1, delay: 0.4 }}
        >
          Tesla Academy.
        </motion.h1>

        <div className="mt-4">
          <ReactTyped 
            strings={['SCIENCE...', 'MATHEMATICS...', 'SOCIAL STUDIES...']} 
            className="md:text-3xl sm:text-2xl text-2xl p-2 font-bold text-teal-500"
            typeSpeed={50} 
            backSpeed={80} 
            loop
          />
        </div>
      </div>

      {/* Image Content */}
      <motion.div 
        className="w-full lg:w-1/2 flex justify-center items-center p-4" // Added padding for small screens
        initial={{ opacity: 0, scale: 0.8 }} 
        animate={{ opacity: 1, scale: 1 }} 
        transition={{ duration: 1, delay: 0.6 }}
      >
        <img 
          src={hero} 
          alt="Description of the image" 
          className="w-3/4 sm:w-1/2 lg:w-3/4 h-auto object-contain rounded-lg shadow-lg transform hover:scale-105 transition-all" // Adjusted sizes for small screens
        />
      </motion.div>
    </div>
  );
};

export default Hero;
