"use client";
import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminEnquiry = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [error, setError] = useState(null);

  // Fetch enquiries when the component mounts
  useEffect(() => {
    const fetchEnquiries = async () => {
      try {
        const response = await axios.get("http://localhost:8000/enquiries"); // Replace with your backend URL
        setEnquiries(response.data);
      } catch (err) {
        setError("Failed to fetch enquiries. Please try again later.");
      }
    };

    fetchEnquiries();
  }, []);

  // Handle the deletion of an enquiry by ID
  const handleDelete = async (id) => {
    try {
      // Send a DELETE request to the backend
      await axios.delete(`http://localhost:8000/enquiries/${id}`); // Replace with your backend URL

      // Update the state to remove the deleted enquiry from the UI
      setEnquiries((prevEnquiries) =>
        prevEnquiries.filter((enquiry) => enquiry._id !== id)
      );
    } catch (err) {
      setError("Failed to delete enquiry. Please try again later.");
    }
  };

  return (
    <div className="max-w-7xl mx-auto p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold text-blue-600 text-center mb-8">Admin Enquiries</h1>

      {error && <p className="text-red-500 text-center mb-4">{error}</p>}

      <div className="overflow-x-auto">
        <table className="table-auto w-full bg-white shadow rounded">
          <thead>
            <tr className="bg-teal-400 text-white ">
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Class</th>
              <th className="px-4 py-2">Board</th>
              <th className="px-4 py-2">Subject</th>
              <th className="px-4 py-2">Phone</th>
              <th className="px-4 py-2">Message</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {enquiries.length > 0 ? (
              enquiries.map((enquiry, index) => (
                <tr
                  key={enquiry._id} // Use unique ID as the key
                  className={`${index % 2 === 0 ? "bg-gray-100" : "bg-white"} border-b`}
                >
                  <td className="px-4 py-2">{enquiry.name}</td>
                  <td className="px-4 py-2">{enquiry.class_name}</td>
                  <td className="px-4 py-2">{enquiry.board}</td>
                  <td className="px-4 py-2">{enquiry.subject}</td>
                  <td className="px-4 py-2">{enquiry.country} {enquiry.phone}</td>
                  <td className="px-4 py-2">{enquiry.enquiryMessage}</td>
                  <td className="px-4 py-2">
                    <button
                      className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                      onClick={() => handleDelete(enquiry._id)} // Pass ID to handleDelete
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="text-center py-4 text-gray-500">
                  No enquiries found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminEnquiry;
