"use client";
import React, { useState } from "react";
import AdminCourses from "./AdminCourses"; // Import AdminCourses as the default export
import AdminEnquiry from "./AdminEnquiry"; // Import AdminEnquiry
import AdminNavBar from "./AdminNavbar";

export function AdminPanel() {
  const [currentView, setCurrentView] = useState("home"); // State to track the current view

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
      {/* Admin Panel Home */}
      <AdminNavBar setCurrentView={setCurrentView} /> {/* Pass setCurrentView here */}
      {currentView === "home" && (
        <div className="text-center space-y-6">
          {/* The buttons have been moved to AdminNavBar */}
        </div>
      )}

      {/* Courses Management */}
      {currentView === "courses" && (
        <div className="w-full">
          {/* Back Button to Return to Admin Panel */}
          <button
            onClick={() => setCurrentView("home")}
            className="bg-gray-500 text-white px-4 py-2 rounded m-4 hover:bg-gray-600"
          >
            Back to Admin Panel
          </button>
          {/* Render AdminCourses Component */}
          <AdminCourses />
        </div>
      )}

      {/* Enquiry Management */}
      {currentView === "enquiries" && (
        <div className="w-full">
          {/* Back Button to Return to Admin Panel */}
          <button
            onClick={() => setCurrentView("home")}
            className="bg-gray-500 text-white px-4 py-2 rounded m-4 hover:bg-gray-600"
          >
            Back to Admin Panel
          </button>
          {/* Render AdminEnquiry Component */}
          <AdminEnquiry />
        </div>
      )}
    </div>
  );
}

export default AdminPanel;
