"use client";
import React, { useState } from "react";
import AdminCourses from "./AdminCourses"; // Import AdminCourses as the default export
import AdminEnquiry from "./AdminEnquiry"; // Import AdminEnquiry
import logo from "../assets/logo.png";

export function AdminPanel() {
  const [currentView, setCurrentView] = useState("home"); // State to track the current view

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Navbar at the top */}
      <div className="flex flex-col md:flex-row justify-between items-center h-auto px-6 py-4 bg-blue-500 text-white shadow-lg">
        {/* Logo Section */}
        <div className="flex items-center w-full md:w-auto mb-4 md:mb-0">
          <img
            src={logo}
            alt="admin-logo"
            className="h-14 w-14 cursor-pointer transform transition duration-300 hover:scale-110"
          />
          <h1 className="ml-4 text-lg font-bold">Tesla Academy Admin</h1>
        </div>

        {/* Buttons Section */}
        <div className="flex justify-center items-center w-full space-x-4">
          <button
            onClick={() => setCurrentView("home")}
            className={`${
              currentView === "home" ? "bg-blue-700" : "bg-blue-600"
            } text-white rounded-full px-6 py-3 font-medium transform transition-all duration-300 hover:scale-105 hover:shadow-lg`}
          >
            Dashboard
          </button>
          <button
            onClick={() => setCurrentView("courses")}
            className={`${
              currentView === "courses" ? "bg-green-700" : "bg-green-600"
            } text-white rounded-full px-6 py-3 font-medium transform transition-all duration-300 hover:scale-105 hover:shadow-lg`}
          >
            Courses Data
          </button>
          <button
            onClick={() => alert("Button 2 Functionality - Coming Soon!")}
            className="bg-yellow-600 text-white rounded-full px-6 py-3 font-medium transform transition-all duration-300 hover:scale-105 hover:shadow-lg"
          >
            Students Data
          </button>
          <button
            onClick={() => setCurrentView("enquiries")}
            className={`${
              currentView === "enquiries" ? "bg-red-700" : "bg-red-600"
            } text-white rounded-full px-6 py-3 font-medium transform transition-all duration-300 hover:scale-105 hover:shadow-lg`}
          >
            Enquiry Data
          </button>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex-grow flex flex-col items-center justify-center p-6">
        {currentView === "home" && (
          <div className="text-center space-y-6">
            <h1 className="text-4xl font-bold mb-8">Welcome to the Admin Panel</h1>
          </div>
        )}

        {/* Courses Management */}
        {currentView === "courses" && (
          <div className="w-full">
            {/* Back Button to Return to Admin Panel */}
            <button
              onClick={() => setCurrentView("home")}
              className="bg-gray-500 text-white px-4 py-2 rounded m-4 hover:bg-gray-600"
            >
              Back to Dashboard
            </button>
            {/* Render AdminCourses Component */}
            <AdminCourses />
          </div>
        )}

        {/* Enquiry Management */}
        {currentView === "enquiries" && (
          <div className="w-full">
            {/* Back Button to Return to Admin Panel */}
            <button
              onClick={() => setCurrentView("home")}
              className="bg-gray-500 text-white px-4 py-2 rounded m-4 hover:bg-gray-600"
            >
              Back to Dashboard
            </button>
            {/* Render AdminEnquiry Component */}
            <AdminEnquiry />
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminPanel;
